var http = require('http');
var memcached = require('../');

var db = new memcached('127.0.0.1:11211');


var server = http.createServer(function (request, response) {
        response.writeHead(200, {"Content-Type": "text/plain"});

        db.get('memtest', function(e,gr){
                if(e)console.error(e);
                db.increment('memtest', 1, function(e,sr){
                        response.end("memtest: "+gr);
                });
        });
});

db.set('memtest',1, 3600, function(e,r){
        if(e)console.err(e);
        server.listen(8000);
});